<?phpnamespace Home\Model;use Think\Model\RelationModel;class GodModel extends RelationModel {    public function shopAdress($cityId) {        $areas = M('Category')->where('pid=%d', array($cityId))->select();        $shops = array();        $model = D('Admin/Category');        $res = $model->take();        foreach ($areas as $k => $v) {            $shops = array_merge($shops, $model->cat_options($v['id'], $res));        }        return $shops;    }    public function games($gameId) {        $model = D('Admin/Category');        return array_values($model->cat_options($gameId, $model->take()));    }    public function photos($member_id) {        return M('image')->where("type='photo' and member_id=%d", array($member_id))->select();    }}